/**
 * config compare
 */
import React, &#123; useState, useEffect, Fragment &#125; from 'react';
import Layout from 'components/Layout';
import &#123; Row, Select, notification &#125; from 'antd';
import &#123; get &#125; from 'common/xFetch2';
import DiffView from './diff-view';
import styles from './styles.less';

const &#123; Option &#125; = Select;

const ConfigDiff = (&#123; location = &#123;&#125; &#125;) => &#123;
  const &#123; record = &#123;&#125; &#125; = location.state;

  const [loading, setLoading] = useState(false);
  const [versions, setVersions] = useState([]);
  const [diffCountArray, setDiffCountArray] = useState([]);
  const [sourceVersion, setSourceVersion] = useState();
  const [targetVersion, setTargetVersion] = useState();
  const [sourceMeta, setSourceMeta] = useState();
  const [targetMeta, setTargetMeta] = useState();
  const [sourceMap, setSourceMap] = useState(&#123;&#125;); // source text mapping
  const [targetMap, setTargetMap] = useState(&#123;&#125;); // target text mapping

  // get all versions when page loaded.
  useEffect(() => &#123;
    getVersions();
  &#125;, []);

  // fetch source version data when source version changed.
  useEffect(() => &#123;
    if (sourceVersion) &#123;
      getSourceMeta();
    &#125;
  &#125;, [sourceVersion]);

  // fetch target version data when target version changed.
  useEffect(() => &#123;
    if (targetVersion) &#123;
      getTargetMeta();
    &#125;
  &#125;, [targetVersion]);

  // organize source &amp; target mapping data when meta data changed.
  useEffect(() => &#123;
    if (sourceMeta &amp;&amp; targetMeta) &#123;
      const &#123; content: srcContent = [] &#125; = sourceMeta;
      const &#123; content: tarContent = [] &#125; = targetMeta;
      const srcPathSet = new Set(srcContent.map((conf) => conf.path));
      const tarPathSet = new Set(tarContent.map((conf) => conf.path));
      const finalPathSet = new Set([...srcPathSet, ...tarPathSet]);
      const srcMapping = &#123;&#125;;
      const tarMapping = &#123;&#125;;
      srcContent.forEach((conf) => &#123;
        srcMapping[conf.path] = conf;
      &#125;);
      tarContent.forEach((conf) => &#123;
        tarMapping[conf.path] = conf;
      &#125;);
      setSourceMap(srcMapping);
      setTargetMap(tarMapping);
      setDiffCountArray(Array.from(finalPathSet));
    &#125;
  &#125;, [sourceMeta, targetMeta]);

  // fetch all versions.
  const getVersions = async () => &#123;
    setLoading(true);
    const res = await get(
      `/api/auto/v1/auto-app-components-record/versions?component_id=$&#123;record.id&#125;`
    );
    setLoading(false);
    if (res.status !== 'success') &#123;
      return notification.error(&#123; message: res.message &#125;);
    &#125;
    const &#123; list = [] &#125; = res.content;
    setVersions(list);
  &#125;;

  // fetch source meta data.
  const getSourceMeta = async () => &#123;
    setLoading(true);
    const res = await get(
      `/api/auto/v1/auto-app-components-record?version=$&#123;sourceVersion&#125;`
    );
    setLoading(false);
    if (res.status !== 'success') &#123;
      return notification.error(&#123; message: res.message &#125;);
    &#125;
    const &#123; content = &#123; content: [] &#125; &#125; = res;
    setSourceMeta(content);
  &#125;;

  // fetch target meta data.
  const getTargetMeta = async () => &#123;
    setLoading(true);
    const res = await get(
      `/api/auto/v1/auto-app-components-record?version=$&#123;targetVersion&#125;`
    );
    setLoading(false);
    if (res.status !== 'success') &#123;
      return notification.error(&#123; message: res.message &#125;);
    &#125;
    const &#123; content = &#123; content: [] &#125; &#125; = res;
    setTargetMeta(content);
  &#125;;

  return (
    &lt;Layout title=&#123;`版本比较（$&#123;record.name&#125;）`&#125; goBack=&#123;true&#125;>
      &lt;div className=&#123;styles.compareHeader&#125;>
        &lt;div className=&#123;styles.compareTitle&#125;>
          &lt;Select
            showSearch=&#123;true&#125;
            style=&#123;&#123; width: 350 &#125;&#125;
            placeholder='请选择版本'
            onSelect=&#123;(v) => setSourceVersion(v)&#125;
            loading=&#123;loading&#125;
          >
            &#123;versions.map((v) => (
              &lt;Option key=&#123;v&#125; value=&#123;v&#125;>
                &#123;v&#125;
              &lt;/Option>
            ))&#125;
          &lt;/Select>
          &#123;sourceMeta ? (
            &lt;p>&#123;`$&#123;sourceMeta.creator&#125; 于 $&#123;sourceMeta.created_at&#125; 修改`&#125;&lt;/p>
          ) : (
            &lt;p>请选择版本以查看信息&lt;/p>
          )&#125;
        &lt;/div>
        &lt;div className=&#123;styles.headerGap&#125;>版本号&lt;/div>
        &lt;div className=&#123;styles.compareTitle&#125;>
          &lt;Select
            showSearch=&#123;true&#125;
            style=&#123;&#123; width: 350 &#125;&#125;
            placeholder='请选择版本'
            onSelect=&#123;(v) => setTargetVersion(v)&#125;
            loading=&#123;loading&#125;
          >
            &#123;versions.map((v) => (
              &lt;Option key=&#123;v&#125; value=&#123;v&#125;>
                &#123;v&#125;
              &lt;/Option>
            ))&#125;
          &lt;/Select>
          &#123;targetMeta ? (
            &lt;p>&#123;`$&#123;targetMeta.creator&#125; 于 $&#123;targetMeta.created_at&#125; 修改`&#125;&lt;/p>
          ) : (
            &lt;p>选择版本以查看信息&lt;/p>
          )&#125;
        &lt;/div>
      &lt;/div>
      &lt;Row className=&#123;styles.diffContainer&#125;>
        &#123;sourceMeta &amp;&amp; targetMeta ? (
          &lt;div>
            &#123;diffCountArray.map((path, index) => &#123;
              return (
                &lt;Fragment>
                  &lt;div className=&#123;styles.confTitle&#125;>&#123;`配置路径: $&#123;path&#125;`&#125;&lt;/div>
                  &lt;DiffView
                    key=&#123;`$&#123;path&#125;-$&#123;index&#125;`&#125;
                    sourceText=&#123;sourceMap[path] &amp;&amp; sourceMap[path].content&#125;
                    targetText=&#123;targetMap[path] &amp;&amp; targetMap[path].content&#125;
                  />
                &lt;/Fragment>
              );
            &#125;)&#125;
            &lt;div className=&#123;styles.confTitle&#125;>&#123;`发布模板`&#125;&lt;/div>
            &lt;DiffView
              sourceText=&#123;sourceMeta.deploy_tpl || ''&#125;
              targetText=&#123;targetMeta.deploy_tpl || ''&#125;
            />
          &lt;/div>
        ) : (
          &lt;p>请选择两个版本进行比较&lt;/p>
        )&#125;
      &lt;/Row>
    &lt;/Layout>
  );
&#125;;

export default ConfigDiff;

