使用箭头函数特性
// longhand
function calcCircumference(diameter) &#123;
  return Math.PI * diameter;
&#125;

// shorthand
calcCircumference = (diameter) => Math.PI * diameter;

17. 科学计数// longhand
for (var i = 0; i &lt; 10000; i++) &#123; ... &#125;

// shorthand
for (var i = 0; i &lt; 1e4; i++) &#123; ... &#125;

18. 参数默认值// longhand
function add(test1, test2) &#123;
  if (test1 === undefined) test1 = 1;
  if (test2 === undefined) test2 = 2;
  return test1 + test2;
&#125;

// shorthand
